mapper.select:
select user_id, name, email, role, status, created_at, updated_at, last_login FROM users


        AND role = #{role}

    AND status = #{status}
    AND created_at >= #{from}
    AND created_at < #{to}

ORDER BY user_id DESC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE users
name = #{req.name}, email = #{req.email}, role = #{req.role}, status = #{req.status}, updated_at = NOW()
WHERE user_id = #{userId}

mapper.select:
SELECT COUNT(1)
FROM users


        AND role = #{role}


        AND status = #{status}


        AND created_at >= #{from}


        AND created_at < #{to}
